  title: {
    type: String,
    trim: true,
    lowercase: true,
    index: true,
    unique: true,
    required: true,
    maxlength: [100, 'Title too long.']
  }, 
  dataPoints: [dataPointsSchema]
});

export default mongoose.model("Poll", pollSchema);

